// API Service
interface ApiService {
    @POST("auth/login")
    fun login(@Body request: LoginRequest): Call<LoginResponse>

    @GET("orders")
    fun getOrders(
        @Query("skip") skip: Int = 0,
        @Query("limit") limit: Int = 100,
        @Query("include_deleted") includeDeleted: Boolean = false
    ): Call<List<Order>>

    @POST("productions")
